def get_clothes_with_locale(locale):
    clothes = [_ for _ in Clothes.objects.all()]
    [_.set_current_language(locale) for  _ in clothes]
    return clothes


def remove_table():
    for locale, table in (('uk', UKR_TABLE), ('ru', RU_TABLE)):
        clothes = get_clothes_with_locale(locale)
        for elem in clothes:
            elem.description = elem.description.split(table)[0]
            elem.save()


